mapper.update:
update params
set param_name = #{paramName,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=VARCHAR},
  param_value = #{paramValue,jdbcType=LONGVARCHAR}
where param_id = #{paramId,jdbcType=VARCHAR}

mapper.select:
select param_id, param_name, create_time, update_time, param_value
from params
where param_id = #{paramId,jdbcType=VARCHAR}
